CREATE TABLE `disease` (
  `Disease_ID` int(11) NOT NULL,
  `Disease_Name` varchar(250) DEFAULT NULL,
  `Disease_types` text DEFAULT NULL,
  `Disease_info` text DEFAULT NULL,
